import React from 'react';
import Number from './Number';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import About from './About';
import Works from './Works';
import History from './History';

class Main extends React.Component{
    constructor(props){
        super(props);
        this.state={
            count:0,
            isSmile:false,
            isFinished :false
        }
    }


    componentDidUpdate(){
        if(this.state.count >41){
            this.setState({
                count:0
            })
        }
    }
    addCount = ()=>{
        this.setState({
            count : this.state.count+1
        });
    }
    toggleSmile=()=>{
        if((this.state.count+1) % 3 === 0){
            this.setState({
                isSmile:true
            })
        }else if((this.state.count-9) === 3 && this.state.count >10 ){
            this.setState({
                isSmile:true
            })
        }else if((this.state.count-19) === 3 && this.state.count >20 ){
            this.setState({
                isSmile:true
            })
        }else if(this.state.count >=30 && this.state.count <39){
            this.setState({
                isSmile:true
            })
        }
        else{
            this.setState({
                isSmile:false
            })
        }
    }
    toggleFinished=()=>{
        if(this.state.count === 40){
            this.setState({
                isFinished:true
            })
        }else{
            this.setState({
                isFinished:false
            })
        }
    }
    render(){
        return(

            <>
                <Header/>
                <Router>
                    <Route path="/about/" exact component={About} />
                    <Route path="/works/" exact component={Works} />
                    <Route path="/History/" exact component={History} />
                    <Link to="/about/">アバウト</Link>
                    <Link to="/works/">works</Link>
                    <Link to="/history/">History</Link>
                </Router>
                <Number
                    count = {this.state.count}
                    add ={()=>{this.addCount()}}
                    toggleSmile = {()=>{this.toggleSmile()}}
                    isSmile = {this.state.isSmile}
                    isFinished = {this.state.isFinished}
                    toggleFinished =　{()=>{this.toggleFinished()}}
                />
            </>
        )
    }
}

export default Main;
