import React from 'react';
import Number from './Number';

class Main extends React.Component{
    constructor(props){
        super(props);
        this.state={
            count:0
        }
    }
    componentDidMount(){
        document.getElementById('addCounter').addEventListener('click',this.countUp)
    }
    componentDidMount(){
        document.getElementById('downCounter').addEventListener('click',this.countDown)
    }
    countUp = ()=>{
        this.setState({
            count:this.state.count +1
        })
    }
    countDown = ()=>{
        this.setState({
            count : this.state.count-1
        });
    }
    render(){

        return(
            <>
                <Number
                    count = {this.state.count}
                />
            </>
        )
    }
}

export default Main;
