import React from 'react';
import Article from './article';

class Blog extends React.Component{
    constructor(props){
        super(props);
        this.state={
            isPublished: false,
            number :1
        }
    }

    togglePublished = ()=>{
        this.setState({
            isPublished :!this.state.isPublished
        }) ;
    }
    addNumber = ()=>{
        this.setState={
            number : this.state.number+1
        }
    }

    render(){
        return(
            <>
                <Article
                    title = {"react"}
                    isPublished ={this.state.isPublished}
                    toggle = {()=>{this.togglePublished()}}
                    number = {this.state.number}


                />
            </>
        )
    }
}

export default Blog;
