import React from 'react';
import Number from './Number';

class Main extends React.Component{
    constructor(props){
        super(props);
        this.state={
            count:0
        }
    }

    addCount = ()=>{
        this.setState({
            number : this.state.xount+1
        });
    }
    downCount = ()=>{
        this.setState({
            number : this.state.number-1
        });
    }

    render(){

        return(
            <>
                <Number
                    count = {this.state.count}
                    add ={()=>{this.addCount()}}
                    down ={()=>{this.downCount()}}

                />
            </>
        )
    }
}

export default Main;
