import React from 'react';
import Number from './Number';



class Main extends React.Component{
    constructor(props){
        super(props);
        this.state={
            count:0,
            isSmile:false
        }
    }

    addCount = ()=>{
        this.setState({
            count : this.state.count+1
        });
    }
    downCount = ()=>{
        this.setState({
            count : this.state.count-1
        });
    }
    toggleSmile=()=>{
        if(this.state.count%5 === 0){
            this.setState({
            isSmile:true
            })
        }else{
            this.setState({
                isSmile:false
            })
        }
    }
    render(){
        return(
            <>
                <Number
                    count = {this.state.count}
                    add ={()=>{this.addCount()}}
                    down ={()=>{this.downCount()}}
                    toggle = {()=>{this.toggleSmile()}}
                />
            </>
        )
    }
}

export default Main;
